package url

import (
	"strconv"
	"testing"
)

func TestIsURLValid(t *testing.T) {
	tables := []struct {
		name        string
		inputURL    string
		outputValid bool
		outputError string
	}{
		{
			"Case 1: Valid URL without paramters",
			"http://www.google.com",
			true,
			"",
		},
		{
			"Case 2: Valid URL without protocol or paramters",
			"www.google.com",
			true,
			"",
		},
		{
			"Case 3: Valid URL with user-defined params",
			"http://www.google.com?a=123",
			true,
			"",
		},
		{
			"Case 4: Blank URL",
			"",
			false,
			"the URL cannot be blank",
		},
		{
			"Case 5: Max length URL",
			"https://www.google.com/?utm_source=THIS+TEST+NEWSLETTER&utm_campaign=256b7777e7a-newsletter201313&utm_medium=email&utm_term=0_80b5c23cde-256b711e7a-%5BLIST_EMAIL_ID%5D&gooal=eyJjaWQiOiIyNTZiNzExZTdhIiwidGFnIjoibmV3c2xldHRlcjIwMTMwNiIsInVpZCI6IjQ1MTRlYjQxNDg5NGExODczNmMzMDQ2YWQifQ%3D%3D%7CY3Npa29zLmFrb3NAcHJldHR5bGl0dGxlL777&mc_cid=256b711777&mc_eid=%5BUNIQID%",
			false,
			"the URL length cannot exceed " + strconv.Itoa(URLMaxLength) + " characters",
		},
		{
			"Case 6: MIN length URL",
			"ac",
			false,
			"the URL length cannot be less than " + strconv.Itoa(URLMinLength) + " characters",
		},
		{
			"Case 7: prefix '.' in URL",
			".google.com",
			false,
			"the URL cannot have a prefix '.'",
		},
		{
			"Case 8: prefix '.' in URL host",
			"http://.www.google.com",
			false,
			"the URL host cannot have a prefix '.'",
		},
		{
			"Case 9: URL host not present",
			"kjbafjasb",
			false,
			"the URL host is not present",
		},
		{
			"Case 10: Normal URL without paramters but has fragment",
			"http://www.google.com#here",
			false,
			"the URL has a fragment(#) which is not supported",
		},
		{
			"Case 11: Slightly Invalid URL",
			"http://www.goo<gle.com",
			false,
			"the URL is invalid",
		},
		{
			"Case 12: Normal URL with user params (no conflict)",
			"http://www.google.com?a=123",
			true,
			"",
		},
	}
	for _, table := range tables {
		gotValid, gotError := IsURLValid(table.inputURL)
		if gotValid != table.outputValid {
			t.Errorf("%s IsURLValid failed got: `%v`, want `%v` ", table.name, gotValid, table.outputValid)
		}
		if gotError != nil && gotError.Error() != table.outputError {
			t.Errorf("%s IsURLValid failed got: `%s`, want `%s` ", table.name, gotError.Error(), table.outputError)
		}
	}
}
